import SynchronousLink from "./SynchronousLink";
import {GraphQLClient} from "graphql-request";

export type GraphQLLinkConfiguration = {
    headers: any
};

export type GraphQLLinkRunConfiguration = {
    query: string,
    variables: any
};

export default class GraphQLLink extends SynchronousLink {

    client: GraphQLClient;

    /**
     * Construct a new GraphQLLink.
     */
    constructor(url: string, config: GraphQLLinkConfiguration = {}) {
        super(url);
        this.client = new GraphQLClient(url, {headers: config.headers || {}});
    }

    /**
     * Alias for run.
     */
    async query(config: GraphQLLinkRunConfiguration) {
        return this.run(config);
    }

    /**
     * Alias for run.
     */
    async mutate(config: GraphQLLinkRunConfiguration): any {
        return this.run(config);
    }

    /**
     * Run a new apollo request
     */
    async run(config: GraphQLLinkRunConfiguration): any {
        return await this.client.request(config.query, config.variables || {});
    }
}

/**
 * Class representing a simple HTTP server link
 * Author: Jun Zheng
 * License: MIT
 */

const SynchronousLink = require('./SynchronousLink');

const axios = require('axios');

class HttpLink extends SynchronousLink {
    /**
     * Construct the link with URL
     * @param url
     */
    constructor(url){
        super(url);
    }

    /**
     * Run a get request
     * @param config
     * @returns {Promise<Object>}
     */
    get(config = {}){
        return this.run(Object.assign(config, {method: 'get'}));
    }

    /**
     * Run a post request
     * @param config
     * @returns {Promise<Object>}
     */
    post(config = {}){
        return this.run(Object.assign(config, {method: 'post'}));
    }

    /**
     * Run a put request
     * @param config
     * @returns {Promise<Object>}
     */
    put(config = {}){
        return this.run(Object.assign(config, {method: 'put'}));
    }

    /**
     * Run a delete request
     * @param config
     * @returns {Promise<Object>}
     */
    delete(config = {}){
        return this.run(Object.assign(config, {method: 'delete'}));
    }

    /**
     * Runs a new http request
     * @param config
     * @returns {Promise<object>}
     */
    run(config){
        return new Promise((resolve, reject) => {
            axios.request({
                method: config.method,
                url: this._url,
                data: config.data ? config.data : {},
                headers: config.headers ? config.headers: {}
            }).then(data => {
                resolve(data);
            }).catch(e => {
                reject(e);
            })
        });
    }
}

module.exports = HttpLink;

